class Solution {
public:
    int numberOfSubstrings(string s) {
        vector<int> v{-1};
        int n = s.size();
        for(int i=0;i<n;i++) if(s[i]=='0') v.push_back(i);
        v.push_back(n);
        int re = 0;
        for(int i=1;i<v.size();i++) {
            int k = v[i]-v[i-1]-1;
            re+=k*(k+1)/2;
        }
        // cout << v.size() << '\n';
        for(int i=1;i<=min((int)v.size()-2,200);i++){
            for(int j=1;j+i<v.size();j++){
                int l = v[j]-v[j-1]-1, r=v[j+i]-v[j+i-1]-1,m=v[j+i-1]-v[j]+1-i;
                int req = i*i-m;
                if(l>r) swap(l,r);
                // cout << i << ' ' << j << ' ' << req << ' ' << l << ' ' << r << ' ' << re << ' ';
                if(req<=0){
                    re+=(l+1)*(r+1);
                } else if(req<=l){
                    re+=(l+1)*(r+1)-req*(req+1)/2;
                } else if(req<=r){
                    re+=(l+1)*(r+1)-l*(l+1)/2-(req-l)*(l+1);
                } else if(req<=r+l){
                    int k = r+l-req+1;
                    re+=k*(k+1)/2;
                }
                // cout << re << '\n';
            }
        }
        return re;
    }
};
