class Solution {
public:
    int numberOfSubstrings(string s) {
        int N = s.size();
        int T = ceil(sqrt(N));
        cout << N << T << endl;

        int ans = 0;
        for (int tc0 = 0; tc0 <= T; ++tc0) {
            int rmin = -1, rmax = -1, c0 = 0, c0r = 0, c1 = 0;
            for (int l = 0; l < N; ++l) {
                if (rmin < l) {
                    rmin = l;
                    if (l == 0) {
                        rmax = l;
                        c0r = 0;
                    }
                    c0 = 0;
                    c1 = 0;
                }

                while (c0 < tc0 && rmin < N) {
                    if (s[rmin] == '0') ++c0;
                    else ++c1;
                    ++rmin;
                }

                if (c0 < tc0) break;

                while (c0r <= tc0 && rmax < N) {
                    if (s[rmax] == '0') ++c0r;
                    ++rmax;
                }

                int d = rmax - max(l+1, rmin);
                if (rmax == N && c0r <= tc0) ++d;

                if (c1 >= tc0*tc0) {
                    ans += d;
                } else if (c1 + d - 1 >= tc0*tc0) {
                    ans += d - (tc0 * tc0 - c1);
                }

                if (s[l] == '0') {
                    --c0;
                    --c0r;
                } else {
                    --c1;
                }
            }
        }


        return ans;
    }
};
