class Solution:
    def numberOfSubstrings(self, s: str) -> int:
        n = len(s)
        g = [[] for _ in range(n + 1)]
        g[0].append(0)
        ans = c0 = c1 = 0
        for i, x in enumerate(s, 1):
            if x == '0':
                c0 += 1
            else:
                c1 += 1
            g[c0].append(c1)
            for k in range(c0 + 1):
                if k * k + k > i:
                    break
                d0 = c0 - k
                d1 = c1 - k * k
                ans += bisect_right(g[d0], d1)
            # print(i, g, c0, c1, ans)
        return ans - n
