class Solution {
public:
    deque<int> pos;
    int numberOfSubstrings(string s) {
        int ans = 0;
        pos.push_front(-1);
        for (int i = 0; i < s.size(); i++) {
            if (s[i] == '0') {
                pos.push_front(i);
                if (pos.size() > 200) pos.pop_back();
            }
            if (!pos.empty()) ans += i - pos[0];
            for (int j = 0; j + 1 < pos.size(); j++) {
                int dist = pos[j] - pos[j + 1] - 1;
                int lb = i - pos[j] + 1 - (j + 1);
                int ub = lb + dist;
                // cout << j + 1 << ' ' << lb << ' ' << ub << '\n';
                if (ub < (j + 1) * (j + 1)) continue;
                ans += ub - max((j + 1) * (j + 1), lb) + 1;
            }
        }
        return ans;
    }
};
