class Solution {
public:
    int numberOfSubstrings(string s) {
        int i,j,n,ans,cnt0,cnt1,prev,tmp,ll,rr;
        vector<int> ind0;
        n=s.length();
        ind0.clear();
        ind0.push_back(n);
        ans=0;
        for (i=n-1;i>=0;i--)
        {
            if (s[i]=='0') ind0.push_back(i);
            cnt0=0;cnt1=0;
            prev=i-1;
            ans--;
            for (j=ind0.size()-1;j>=0;j--)
            {
                ll=cnt1;
                rr=cnt1+ind0[j]-prev-1;
                //cout<<"ll,rr:"<<ll<<" "<<rr<<" "<<cnt0<<" "<<ind0[j]<<endl;
                if (rr>=cnt0*cnt0)
                {
                    ll=max(ll,cnt0*cnt0);
                    ans+=rr-ll+1;
                }
                cnt0++;
                cnt1=rr;
                prev=ind0[j];
                if (cnt0*cnt0>n-i) break;
            }
        }
        return ans;
    }
};
