#define ll long long int

class Solution {
public:
    int numberOfSubstrings(string a) {
        ll ans = 0;

        ll n = a.size();
        vector<ll> v (n);

        vector<ll> vv;

        for(ll i = 0; i<n; i++){
            if(a[i] == '1'){
                v[i] = 1;
            }
            else{
                vv.push_back(i);
            }

            if(i - 1 >= 0){
                v[i] += v[i - 1];
            }
        }

        v.push_back(v.back());

        vv.push_back(n);

        ll k = 0;

        for(ll i = 0; i<n; i++){
            ll c0 = 0;
            ll prev = i;
            for(ll j = k; j<min((ll) vv.size(), k + 205LL); j++){
                ll c1 = v[vv[j]] - (i-1>=0?v[i - 1]:0);
                if(c1 >= c0 * c0){
                    ll cc1 = v[prev] - (i-1>=0?v[i - 1]:0);

                    if(cc1 >= c0 * c0){
                        ans += vv[j] - prev;
                    }
                    else{
                        ans += c1 - (c0 * c0) + 1;
                    }
                }
                c0++;
                prev = vv[j];
            }

            if(vv[k] == i){
                k++;
            }
        }

        return ans;
    }
};
