class Solution {
    public int numberOfSubstrings(String s) {
        return sln1(s);
    }

    private int sln1(String s){
        int n = s.length();
        int max = (int)Math.sqrt(n)+1;
        int res = 0;
        for(int i = 0;i<=max;i++){
            int x = help(s, i);
            //System.out.printf("%d %d\n", i, x);
            res = res+ x;
        }
        return res;
    }

    private int help(String s, int k){
        int n = s.length();
        int res = 0;
        for(int i = 0, count0a = 0, count0b = 0, ja = 0, jb= 0;i<n;i++){
            count0a += '1'-s.charAt(i);
            count0b += '1'-s.charAt(i);
            while(count0a>k){
                count0a -= '1'-s.charAt(ja++);
            }
            while(count0b>k || (jb<=i && count0b== k && i-jb+1-count0b >= count0b*count0b)){
                count0b -= '1'-s.charAt(jb++);
            }
            if(count0a == k){
                res+= jb-ja;
            }
        }
        return res;
    }
}
