class Solution {
public:
    int numberOfSubstrings(string s) {
        int n = s.size();
        vector<int> jidx(n, 1);
        for (int i = n - 2; i >= 0; --i) {
            if (s[i + 1] == s[i]) {
                jidx[i] = jidx[i + 1] + 1;
            }
        }


        int total = 0;
        for (int i = 0; i != n; ++i) {
            int zero = 0, one = 0;
            int j = i;
            while (zero <= 200 && j < n) {
                if (s[j] == '0') {
                    int hi = sqrt(one);
                    total += max(min(jidx[j], hi - zero), 0);
                    zero += jidx[j];
                } else {
                    int lo = zero * zero;
                    total += max(min(jidx[j], one + jidx[j] - lo + 1), 0);
                    one += jidx[j];
                }
                j += jidx[j];
            }
        }

        return total;
    }
};
