class Solution:
    def numberOfSubstrings(self, s: str) -> int:
        ans = 0
        a, b = [], []
        for i in range(len(s)):
            if s[i] == '0':
                a.append(i)
            else:
                b.append(i)
                ans += (i+1) if not a else i-a[-1]
            for z in range(1, min(isqrt(len(b)), len(a))+1):
                l = a[len(a)-(z+1)]+1 if z != len(a) else 0
                r = a[len(a)-z]
                u = b[len(b)-z*z]
                if u < l: continue
                if u >= r:
                    ans += r-l+1
                else:
                    ans += u-l+1



        return ans
