class Solution {
public:
    int cnt,ls,nw;
    int numberOfSubstrings(string s) {
        x.clear();
        ans = 0;
        for(auto i:s){
            if(i=='1')++cnt, ans+= cnt;
            else {
                x.pb(cnt);
                cnt = 0;
            }
        }
        x.pb(cnt);
        n = x.size()-1;
        For(i,0,n){
            nw = 0;
            For(j,i,n){
                a = x[i];
                b = x[j+1];
                y = (j-i+1)*(j-i+1);
                if(y>s.size())break;
                z = y-nw;
                if(z<=0)ans+= (a+1)*(b+1);
                else {
                    res = (a+1)*(b+1);
                    if(a+b>=z){
                        // cout<<a<<" "<<b<<" "<<z<<endl;
                        // cout<<i<<","<<j<<" = ";
                        if(a>b)swap(a,b);
                        a = min(a,z-1);
                        // cout<<a<<" ";
                        rep(i,0,a){
                            if(i+b<z)res-= b+1;
                            else res-= z-i;
                        }
                        // cout<<res<<endl;
                        ans+= res;
                    }
                }
                nw+= x[j+1];
            }
        }
        return ans;
    }
};
