from sortedcontainers import SortedList

class Solution:
    def numberOfSubstrings(self, s: str) -> int:
        n = len(s)
        cur = 0
        stat = []
        for c in s:
            if c == '1':
                cur += 1
            stat.append(cur)
        zeros = collections.deque()
        zeros.appendleft(collections.deque())
        max_zero = int(math.sqrt(n))

        ans = 0
        for i in range(n)[::-1]:
            cur = stat[i]
            for zero in range(min(len(zeros), max_zero + 1)):
                if len(zeros[zero]) == 0:
                    continue
                ans += len(zeros[zero]) - bisect.bisect_left(zeros[zero], cur + zero ** 2)
            if s[i] == '1':
                zeros[0].appendleft(stat[i])
            else:
                zeros[0].appendleft(stat[i])
                zeros.appendleft(collections.deque())

        cur = 0
        for zero in range(min(len(zeros), max_zero + 1)):
            if len(zeros[zero]) == 0:
                continue
            ans += len(zeros[zero]) - bisect.bisect_left(zeros[zero], cur + zero ** 2)

        return ans
