class Solution {
public:
    int numberOfSubstrings(string s) {
        int n = s.size();
        unordered_map<int, vector<int>> freq_map;
        freq_map[0].push_back(-1);

        int prefix_sum = 0, ones_count = 0, result = 0;
        for (int i = 0; i < n; ++i) {
            char c = s[i];

            if (c == '1') ++ones_count;
            else --prefix_sum;

            for (int zeroes = 0; zeroes * zeroes <= ones_count; ++zeroes) {
                const auto& current = freq_map[prefix_sum + zeroes];
                int count = upper_bound(current.begin(), current.end(), i - zeroes - zeroes * zeroes) - current.begin();
                result += count;
            }

            freq_map[prefix_sum].push_back(i);
        }

        return result;
    }
};
