class Solution {
public:
    int f(int a, int b, int c, int d) {
        return max(0,min(b,d)-max(a,c)+1); 
    }
    int numberOfSubstrings(string s) {
        int k=0,n=s.size();
        while (k*k<n) k++; 
        vector<int> v;
        for (int i = 0; i < n; i++) {
            if (s[i]-'1') v.push_back(i); 
        }
        v.push_back(n);
        int j=0,m=v.size(),ans=0; 
        for (int i = 0; i < n; i++) {
            if (j<v.size()) {
                ans+=v[j]-i;
                for (int l = j+1; l < min(m,j+k+2); l++) {
                    int w=(l-j)*(l-j); 
                    ans+=f(v[l-1],v[l]-1,i+w+l-j-1,n-1);
                }
                if (i==v[j]) j++; 
            }
            else ans+=n-i; 
            //cout << ans << " "; 
        }
        return ans;
    }
};
