class Solution:
    def numberOfSubstrings(self, s: str) -> int:
        zeros = []
        n = len(s)
        res = 0
        for i, c in enumerate(s):
            c = int(c)           
            if c == 0:
                zeros.append(i)
            sz = 0
            so = 0
            j = len(zeros) - 1

            k = i
            while i >= 0:
                if j >= 0 and i == zeros[j]:
                    j -= 1
                    i -= 1
                    sz += 1
                    if so >= sz**2:
                        res += 1
                    # print('t', k, res)
                else:
                    next_z = zeros[j] if j >= 0 else -1
                    nt = max(sz**2 - so - 1, 0)
                    so += i - next_z    

                    if so >= sz**2:
                        res += i - next_z - nt
                    # print(k, nt, so, res)
                    i = next_z
                if sz ** 2 > n:
                    break
            # print(k, res)
        return res
