class Solution:
    def numberOfSubstrings(self, s: str) -> int:
        n = len(s)
        stack = []
        cur = 0
        ans = 0
        for i in range(n):
            if s[i] == '0':
                stack.append(cur)
                cur = 0
            else:
                cur += 1

            ans += cur
            tmp = cur

            for j in range(1, int(n ** 0.5) + 1):
                if j > len(stack):
                    break
                ans += max(stack[-j] + 1 - max(j * j - tmp, 0), 0)
                tmp += stack[-j]
        return ans
