class Solution {
public:
    int numberOfSubstrings(string s) {
        vector<int> z;
        z.push_back(-1);
        int n = s.size();
        int res = 0;
        for(int i = 0; i < n; i++){
            if(s[i] == '0'){
                z.push_back(i);
            }
            int len = z.size();
            res += i - z.back();
            for(int j = len - 1; j > 0; j--){
                long long q0 = len - j;
                int qi = (i - z[j] + 1) - q0;
                int can = z[j] - z[j - 1] - 1;
                if(q0 * q0 <= qi){
                    res += can + 1;
                } else {
                    long long po = q0 * q0 - qi;
                    if(po <= can){
                        res += can - po + 1;
                    }
                }
                if(q0 * q0 > n){
                    break;
                }
            }
        }
        return res;
    }
};
