class Solution:
    def numberOfSubstrings(self, s: str) -> int:
        n = len(s)
        k = ceil(sqrt(n))
        idxs = deque()  # decreasing order
        ans = 0
        for i in range(n):
            if s[i] == '0':
                if len(idxs) >= k:
                    idxs.pop()
                idxs.appendleft(i)
            m = len(idxs)
            for j in range(-1, m):
                idx = idxs[j] if j >= 0 else i
                l = idxs[j+1] if j < m - 1 else -1
                r = min(i - ((j + 1) ** 2 + j + 1) + 1, idx)
                ans += max(r, l) - l
            #     print(i, j, idx, l, r)
            # print(idxs, ans)
        return ans
