class Solution {
public:
    int numberOfSubstrings(string s) {
        int n = s.size();
        vector<int> z;
        for (int i = 1; i <= n; i++) {
            if (s[i - 1] == '0') z.push_back(i);
        }
        z.push_back(n + 1);
        vector<int> psa(n + 1);
        for (int i = 1; i <= n; i++) {
            psa[i] = s[i - 1] - '0' + psa[i - 1];
        }
        int ans = 0;
        for (int i = 1, i1 = 0; i <= n; i++) {
            while(z[i1] < i) i1++;
            if (s[i - 1] == '1') ans += z[i1] - i;
            for (int j = i1, k = 1; j < (int)z.size() - 1 && k * k <= n; j++, k++) {
                int j1 = max(z[j], i + k * (k + 1) - 1);
                if (j1 >= z[j + 1]) continue;
                ans += z[j + 1] - j1;
            }
        }
        return ans;
    }
};
