class Solution {
public:
    int numberOfSubstrings(string s) {
        const int len = s.length();
        int res = 0;
        vector<int> zs(len+1, 0);
        vector<int> os(len+1, 0);
        for(int i = 0; i < len; i++) {
            if(s[i] == '0') zs[i+1] = zs[i] + 1, os[i+1] = os[i];
            else zs[i+1] = zs[i], os[i+1] = os[i] + 1;
        }
        for(int l = 1; l <= len; l++) {
            for(int r = l; r <= len; r++) {
                int z = zs[r] - zs[l-1];
                int o = os[r] - os[l-1];
                int z2 = z*z;
                // cout << l << ", " << r << endl;
                if(z2 <= o) {
                    int j = int(sqrt(o)) - z;
                    // cout << "+ " << j + 1 << endl;
                    // cout << "j " << j << endl;
                    res += j + 1;
                    r += j;
                    if(r > len) res -= r - len;
                } else {
                    r += z2 - o - 1;
                    // cout << "j " << z2 - o - 1 << endl;
                }
            }
        }
        return res;
    }
};
