class Solution {
public:
    int numberOfSubstrings(string s) {
        const int n = (int)s.size();
        vector<int> idxs;
        int ans = 0;
        idxs.push_back(-1);
        for (int i = 0; i < n; ++i) {
            if (s[i] == '0') {
                idxs.push_back(i);
            }
            int z = 0;
            int r = i;
            for (int j = (int)idxs.size() - 1; j >= 0; --j) {
                if (z * z > i + 1) {
                    break;
                }
                if (idxs[j] < i) {
                    int cur = r - idxs[j];
                    cur = min(cur, max(0, i - idxs[j] - (z + z * z) + 1));
                    ans += cur;
                }
                r = idxs[j];
                ++z;
            }
        }
        return ans;
    }
};
