class Solution:
    def numberOfSubstrings(self, s: str) -> int:
        #say a 0s, b 1s
        #b >= a*a
        #total length >= a*a+a 
        #n >= a*a+a
        #a <= 200
        answer = 0
        zeroes = []
        n = len(s)
        for i in range(n):
            if s[i]=='0':
                zeroes.append(i) 
        m = len(zeroes)
        if len(zeroes)==0:
            blocks = [n]
        else:
            blocks = []
            blocks.append(zeroes[0])
            for i in range(m-1):
                blocks.append(zeroes[i+1]-zeroes[i]-1)
            blocks.append(n-1-zeroes[-1])
        for x in blocks:
            answer+=x*(x+1)//2
        for a in range(1, 201):
            for i in range(m):
                if i+a-1 <= m-1:
                    left_zero = zeroes[i]
                    right_zero = zeroes[a-1+i]
                    length = right_zero-left_zero+1
                    one_count = length-a
                    if i==0:
                        avail_left = zeroes[i]
                    else:
                        avail_left = zeroes[i]-zeroes[i-1]-1
                    if a-1+i==m-1:
                        avail_right= n-1-zeroes[a-1+i]
                    else:
                        avail_right =  zeroes[a+i]-zeroes[a+i-1]-1
                    if one_count >= a*a:
                        needed = 0
                    else:
                        needed = a*a-one_count
                   # print(a,  left_zero, right_zero, avail_left, avail_right, one_count, needed)
                    entry = 0
                    if avail_left+avail_right >= needed:
                        for x in range(avail_left+1):
                            if 0 <= needed-x <= avail_right:
                                entry+=(avail_right-(needed-x)+1)
                            elif needed-x < 0:
                                entry+=(avail_right+1)
                   # print(entry)
                    answer+=entry
        return answer

