class Solution:
    def numberOfSubstrings(self, s: str) -> int:
        ans = 0 
        vals = [-1]
        for i, ch in enumerate(s): 
            cnt = 0 
            if ch == '1': 
                zero = 0
                one = 1
            else: 
                zero = 1
                one = 0 
            jj = i 
            for j in reversed(vals): 
                one += jj-j-1
                cnt += max(0, min(jj-j, one-zero**2+1))
                jj = j 
                zero += 1
                if zero**2 > i: break 
            ans += cnt
            if ch == '0': vals.append(i)
            # print(i, ch, cnt, ans)
        return ans
