class Solution {
public:
    int numberOfSubstrings(string s) {
        vector<int> idx0;
        const int n = s.size();
        idx0.push_back(-1);
        for (int i = 0; i < n; ++i) {
            if (s[i] == '0') idx0.push_back(i);
        }
        idx0.push_back(n);
        int ans = 0;
        for(int i = 0; i + 1 < idx0.size(); ++i) {
            int tt = idx0[i+1] - idx0[i] - 1;
            ans += tt * (tt + 1) / 2;
        }
        // cout << ans << endl;
        for (int cnt = 1; cnt <= 200; ++cnt) {
            for (int i = 1; i + cnt < idx0.size(); ++i) {
                int j = i + cnt - 1;
                int min_len = cnt * cnt + cnt;
                for (int k = idx0[i]; k > idx0[i-1]; --k) {
                    int rr = k + min_len - 1;
                    if (rr < idx0[j+1]) {
                        ans += idx0[j+1] - max(rr, idx0[j]);
                        // cout << k << ' ' << rr << endl;
                    }
                }
            }
        }
        return ans;
    }
};
