class Solution {
public:
bool jai_shree_ram(int X, int Y, vector<vector<int>>& c0) {

        int n = c0.size();
        vector<vector<int>> v(n + 4);

        for (int i = 0; i < n; i++) {

            if (c0[i][0] <= c0[i][2]) {

                v[n].push_back(i);

                v[i].push_back(n);
            }
            if (X - c0[i][0] <= c0[i][2]) {

                v[n + 2].push_back(i);

                v[i].push_back(n + 2);
            }
            if (c0[i][1] <= c0[i][2]) {

                v[n + 1].push_back(i);

                v[i].push_back(n + 1);
            }
            if (Y - c0[i][1] <= c0[i][2]) {

                v[n + 3].push_back(i);

                v[i].push_back(n + 3);
            }
            for (int j = i + 1; j < n; ++j) {

                double dist = sqrt(pow(c0[i][0] - c0[j][0], 2) + pow(c0[i][1] - c0[j][1], 2));

                int radSum = c0[i][2] + c0[j][2];

                if (radSum >= dist) {

                    v[i].push_back(j);
                    v[j].push_back(i);
                }
            }
        }

        int isBlocked = 0;

        isBlocked = max(isBlocked, bfs(v, n, n + 1, n + 2, n + 4));

        isBlocked = max(isBlocked, bfs(v, n + 3, n + 2, n + 1, n + 4)); 


        return isBlocked == 0;
    }

    int bfs(vector<vector<int>>& g, int s, int t1, int t2, int n) {

        queue<int> q;

        vector<bool> vis(n + 4, false);

        q.push(s);

        vis[s] = true;

        while (!q.empty()) {

            int u = q.front();
            q.pop();

            for (int v : g[u]) {

                if (!vis[v]) {
                    q.push(v);
                    vis[v] = true;
                }
            }
        }

        return (vis[t1] || vis[t2] ? 1 : 0);
    }


    bool canReachCorner(int X, int Y, vector<vector<int>>& circles) {

        return jai_shree_ram(X, Y, circles);
    }
};
