class Solution {
public:
    int helper(vector<vector<int>>& c, int p, int x, int y, int n) {
        queue<int> qu;
        vector<int> visi(n+4, 0);
        qu.push(p);
        visi[p]=1;
        while(!qu.empty()) {
            int first= qu.front();
            qu.pop();
            for(auto it: c[first]) {
                if(!visi[it]) {
                    qu.push(it);
                    visi[it]=1;
                }
            }
        }
        return visi[x] || visi[y];
    }

    bool canReachCorner(int X, int Y, vector<vector<int>>& circles) {
        int size= circles.size();
        vector<pair<pair<int,int>, int>> ds(size);
        vector<vector<int>> c(size +4);

        for(int i=0; i<size;++i) {
            ds[i] = {{circles[i][0], circles[i][1]}, circles[i][2]};
        }

        for(int i=0;i<size;++i) {
            if(ds[i].first.first <= ds[i].second) {
                c[size].push_back(i);
                c[i].push_back(size);
            }

            if(X - ds[i].first.first <= ds[i].second) {
                c[size +2].push_back(i);
                c[i].push_back(size +2);
            }

            if(ds[i].first.second <= ds[i].second) {
                c[size+1].push_back(i);
                c[i].push_back(size+1);
            }

            if(Y - ds[i].first.second <= ds[i].second) {
                c[size +3].push_back(i);
                c[i].push_back(size +3);
            }

            for(int j=i+1;j<size;++j) {
                double val = sqrt(pow(ds[i].first.first - ds[j].first.first, 2) 
                + pow(ds[i].first.second - ds[j].first.second, 2));

                if(ds[i].second + ds[j].second >= val) {
                    c[i].push_back(j);
                     c[j].push_back(i);
                }
            }
        }

        return !(helper(c, size, size+1, size+2, size+4)
                 || helper(c, size+3, size+2, size+1, size+4));
    }
};
