class Solution {
public:

    int func (vector<vector<int>> circle, int a, int b , int c, int d)
    {
        vector<int> visited(d+4,0);
        queue<int> q;
        q.push(a);
        visited[a]=1;
        while (!q.empty())
        {
            int x = q.front();
            q.pop();
            for (int it : circle[x])
            {
                if (!visited[it])
                {
                    visited[it]=true;
                    q.push(it);
                }
            }
        }
        return visited[b] || visited[c];
    }
    bool canReachCorner(int X, int Y, vector<vector<int>>& circles) {

        int n = circles.size();
        vector<pair<pair<int,int>,int>> v(n);
        vector<vector<int>> s(n+4);

        for (int i=0 ; i<n; i++)
        {
            v[i]={{circles[i][0],circles[i][1]},circles[i][2]};
        }

        for (int i=0 ; i<n; i++)
        {
            for (int j=i+1; j<n; j++)
            {
                double xx = sqrt(pow(v[i].first.first-v[j].first.first,2)+pow(v[i].first.second-v[j].first.second,2));
                if (v[i].second+v[j].second>=xx)
                {
                    s[i].push_back(j);
                    s[j].push_back(i);
                }
            }
            if (v[i].first.first<=v[i].second)
            {
                s[i].push_back(n);
                s[n].push_back(i);
            }
            if (v[i].first.second<=v[i].second)
            {
                s[n+1].push_back(i);
                s[i].push_back(n+1);
            }
            if (Y-v[i].first.second<=v[i].second)
            {
                s[n+3].push_back(i);
                s[i].push_back(n+3);
            }
            if (X-v[i].first.first<=v[i].second)
            {
                s[n+2].push_back(i);
                s[i].push_back(n+2);
            }
        }
        return !(func(s,n,n+1,n+2,n+4) || func(s,n+3,n+2,n+1,n+4) );

    }
};
