typedef long long int ll;
class Solution {
public:
    bool dfs(int src, vector<int> v[], vector<bool>& vis, int dest) {
        if(src == dest)     return true;
        vis[src] = true;
        bool ans = 0;
        for(auto it : v[src]) {
            if(vis[it])     continue;
            ans |= dfs(it, v, vis, dest);
        }
        return ans;
    }
    bool canReachCorner(int X, int Y, vector<vector<int>>& circles) {
        vector<int> v[1005];
        for(int i = 0; i < circles.size(); i ++) {

            int mx = circles[i][0] - circles[i][2], mxx = circles[i][0] + circles[i][2];
            int my = circles[i][1] - circles[i][2], myy = circles[i][1] + circles[i][2];
            if(mxx < 0 or mx > X)   continue;
            if(myy < 0 or my > Y)   continue;

            //cout << "III " << circles[i][0] << " "<< circles[i][1] << " "<< circles[i][2] << endl;
            if(circles[i][0] <= circles[i][2]) {
                //cout << i << " "<< 1001 << endl;
                v[i].push_back(1001);
                v[1001].push_back(i);
            }
            if(circles[i][1] <= circles[i][2]) {
                //cout << i << " "<< 1002 << endl;
                v[i].push_back(1002);
                v[1002].push_back(i);
            }

            if(abs(circles[i][0] - X) <= circles[i][2]) {
                //cout << i << " "<< 1003 << endl;
                v[i].push_back(1003);
                v[1003].push_back(i);
            }
            if(abs(circles[i][1] - Y) <= circles[i][2]) {
                //cout << i << " "<< 1004 << endl;
                v[i].push_back(1004);
                v[1004].push_back(i);
            }
            for(int j = i + 1; j < circles.size(); j ++) {

                ll d = (ll)(circles[i][0] - circles[j][0]) * (ll)(circles[i][0] - circles[j][0]);
                d += (ll)(circles[i][1] - circles[j][1]) * (ll)(circles[i][1] - circles[j][1]);

                ll r2 = (ll)(circles[i][2] + circles[j][2]) * (ll)(circles[i][2] + circles[j][2]);

                if(d <= r2) {
                    //cout << i << " "<< j << endl;
                    v[i].push_back(j);
                    v[j].push_back(i);
                }
            }
        }
        vector<bool> vis(1005, false);
        vector<bool> vis2(1005, false);
        vector<bool> vis3(1005, false);
        vector<bool> vis4(1005, false);
        bool ans = dfs(1001, v, vis, 1002) or dfs(1003, v, vis2, 1004);
        ans |= dfs(1001, v, vis3, 1003) or dfs(1002, v, vis4, 1004);
        return !ans;
    }
};

