class Solution:
    def canReachCorner(self, X: int, Y: int, circles: List[List[int]]) -> bool:
        touch_left = [False] * len(circles)
        touch_right = [False] * len(circles)
        touch_top = [False] * len(circles)
        touch_bottom = [False] * len(circles)

        left_top = [False] * len(circles)
        right_bottom = [False] * len(circles)

        for i in range(len(circles)):
            x, y, r = circles[i]
            if (x-X)**2+(y-Y)**2 <= r**2:
                touch_top[i] = True
                touch_right[i] = True
            if x**2+y**2 <= r**2:
                touch_left[i] = True
                touch_bottom[i] = True
            if x**2+(y-Y)**2 <= r**2:
                touch_left[i] = True
                touch_top[i] = True
            if (x-X)**2+y**2 <= r**2:
                touch_right[i] = True
                touch_bottom[i] = True

            if x <= X and abs(y-Y) <= r:
                touch_top[i] = True
            if x <= X and y <= r:
                touch_bottom[i] = True
            if y <= Y and abs(x-X) <= r:
                touch_right[i] = True
            if y<= Y and x <= r:
                touch_left[i] = True

            if touch_left[i] or touch_top[i]:
                left_top[i] = True

            if touch_right[i] or touch_bottom[i]:
                right_bottom[i] = True


        # print(touch_left)
        # print(touch_right)
        # print(touch_top)
        # print(touch_bottom)

        for i in range(len(circles)):
            x1, y1, r1 = circles[i]
            for j in range(i):
                x2, y2, r2 = circles[j]
                if (x1-x2)**2+(y1-y2)**2 <= (r1+r2)**2:
                    left_top[i] = any((left_top[i], left_top[j]))
                    right_bottom[i] = any((right_bottom[i], right_bottom[j]))

            if left_top[i] and right_bottom[i]:
                        return False
        return True
