/**
 * @param {number} X
 * @param {number} Y
 * @param {number[][]} circles
 * @return {boolean}
 */

var canReachCorner = function(X, Y, circles) { 
    function bfs(graph, start, target1, target2, n) { 
        const queue = []; 
        const visited = Array(n + 4).fill(false); 
        queue.push(start); 
        visited[start] = true; 

        while (queue.length > 0) { 
            const u = queue.shift(); 
            for (const v of graph[u]) { 
                if (!visited[v]) { 
                    queue.push(v); 
                    visited[v] = true; 
                } 
            } 
        } 

        return visited[target1] || visited[target2]; 
    } 

    const nc = circles.length; 
    const graph = Array(nc + 4).fill(null).map(() => []); 
    const c = circles.map(circle => [circle[0], circle[1], circle[2]]); 

    for (let i = 0; i < nc; ++i) { 
        if (c[i][0] <= c[i][2]) { 
            graph[nc].push(i); 
            graph[i].push(nc); 
        } 
        if (X - c[i][0] <= c[i][2]) { 
            graph[nc + 2].push(i); 
            graph[i].push(nc + 2); 
        } 
        if (c[i][1] <= c[i][2]) { 
            graph[nc + 1].push(i); 
            graph[i].push(nc + 1); 
        } 
        if (Y - c[i][1] <= c[i][2]) { 
            graph[nc + 3].push(i); 
            graph[i].push(nc + 3); 
        } 
        for (let j = i + 1; j < nc; ++j) { 
            const dx = c[i][0] - c[j][0]; 
            const dy = c[i][1] - c[j][1]; 
            const distance = Math.sqrt(dx * dx + dy * dy); 
            if (c[i][2] + c[j][2] >= distance) { 
                graph[i].push(j); 
                graph[j].push(i); 
            } 
        } 
    } 

    return !(bfs(graph, nc, nc + 1, nc + 2, nc + 4) || 
             bfs(graph, nc + 3, nc + 2, nc + 1, nc + 4)); 
};
