class Solution {
public:

    void dfs(int i, vector<int>adj[], int &l, int &r, int &u, int &b,vector<vector<int>>& c, vector<int>&vis)
    {
        if (c[i][0]-c[i][2]<l) l = c[i][0]-c[i][2];
        if (c[i][0]+c[i][2]>r) r = c[i][0]+c[i][2];
        if (c[i][1]-c[i][2]<b) b = c[i][1]-c[i][2];
        if (c[i][1]+c[i][2]>u) u = c[i][1]+c[i][2];

        vis[i]=1;
        for (auto &j:adj[i])
        {
            if (!vis[j])
            {
                dfs(j,adj,l,r,u,b,c, vis);
            }
        }


    }
    bool canReachCorner(int x, int y, vector<vector<int>>& c) {
        int n=c.size();
        vector<int>adj[n];
        for (int i=0;i<c.size();i++)
        {
            int a = c[i][0], b=c[i][1], r=c[i][2];
            if (1ll*r*r>=1ll*a*a + 1ll*b*b)return false;
            if (1ll*r*r>=1ll*abs(a-x)*abs(a-x) + 1ll*abs(b-y)*abs(b-y))return false;
            for (int j=i+1;j<c.size();j++)
            {
                if (1ll*abs(a-c[j][0])*abs(a-c[j][0]) + 1ll*abs(b-c[j][1])*abs(b-c[j][1])<=1ll*(r+c[j][2])*(r+c[j][2]))
                {
                    adj[i].push_back(j);
                    adj[j].push_back(i);
                }
            }

        }
        vector<int>vis(n);
        // agr koi v dfs 0 to y ya o to x chhek le ho gaya aur kya

        for (int i=0;i<n;i++)
        {
            if (!vis[i])
            {
                int l = c[i][0]-c[i][2], r = c[i][0]+c[i][2], u = c[i][1]+c[i][2], b = c[i][1]-c[i][2];
                dfs(i,adj,l,r,u,b,c, vis); 
                if (u>=y && r>=x)return false;
                if (l<=0 && r>=x)return false;
                if (u>=y && b<=0)return false;
                if (l<=0 && b<=0)return false;
            }
        }
        return true;

    }
};
