vector<int> par(1001, 0);
class Solution {
public:
    int find(int v) {
        if (v == par[v])
            return v;
        par[v]=find(par[v]);
        return par[v];
    }

    void unions(int a, int b) {
        a = find(a);
        b = find(b);
        par[b] = a;
    }
    bool check(vector<int>& a, vector<int>& b) {
        long long dx=b[0]-a[0];
        long long dy=b[1]-a[1];
        long long r=b[2]+a[2];

        return dx*dx+dy*dy<=r*r;
    }
    vector<bool> check2(vector<int>& a, int x, int y) {
        vector<bool> an(4,0);
        if(a[0]-a[2]<=x && a[0]+a[2]>=x)
        an[0]=1;
        if(a[1]-a[2]<=y && a[1]+a[2]>=y)
        an[1]=1;
        if(a[0]-a[2]<=0 && a[0]+a[2]>=0)
        an[2]=1;
        if(a[1]-a[2]<=0 && a[1]+a[2]>=0)
        an[3]=1;
        return an;

    }

    bool canReachCorner(int X, int Y, vector<vector<int>>& c) {
        int n=c.size();
        for(int i=0;i<n;i++) {
            par[i]=i;
        }
        for(int i=0;i<n;i++) {
            for(int j=0;j<i;j++) {
                if(check(c[i], c[j])) unions(i,j);
            }
        }
        vector<vector<bool>>an(n,vector<bool>(4,0));
        for(int i=0;i<n;i++) {
            vector<bool> x=check2(c[i],X,Y);
            for(int j=0;j<4;j++) {
                an[find(par[i])][j]=an[find(par[i])][j] | x[j];
            }
        }
        for(int i=0;i<n;i++) {
            if((an[i][0] && an[i][1]) || (an[i][0] && an[i][2]) || (an[i][2] && an[i][3]) || (an[i][1]&&an[i][3])) return false;
        }
        return true;
    }
};
