class Solution {
public:
int  bf(vector<vector<int> >& arr,int p1,int p2,int p3,int n){
    queue<int>q;
    vector<int>visited(n+4,0);
    q.push(p1);
    visited[p1] = 1;
    while(!q.empty()){
        int u = q.front();
        q.pop();
        for(int v:arr[u]){
            if(!visited[v]){
                q.push(v);
                visited[v] = 1;
            }
        }
    }
    return visited[p2]||visited[p3];
}

    bool canReachCorner(int X, int Y, vector<vector<int>>& circles) {
         int nocircle = circles.size();
         vector<pair<pair<int,int>,int> >circle(nocircle);
         vector<vector<int> >g(nocircle+4);
         for(int i=0;i<nocircle;i++){
            circle[i] = {{circles[i][0],circles[i][1]},circles[i][2]};
         }
         for(int i=0;i<nocircle;i++){
            if(circle[i].first.first <= circle[i].second){
                g[nocircle].push_back(i);
                g[i].push_back(nocircle);
            }
            if(X-circle[i].first.first<=circle[i].second){
                g[nocircle+2].push_back(i);
                g[i].push_back(nocircle+2);
            }
            if(circle[i].first.second<=circle[i].second){
                g[nocircle+1].push_back(i);
                g[i].push_back(nocircle+1);
            }
            if(Y-circle[i].first.second<=circle[i].second){
                g[nocircle+3].push_back(i);
                g[i].push_back(nocircle+3);
            }
            for(int j=i+1;j<nocircle;j++){
                double d = sqrt(pow(circle[i].first.first-circle[j].first.first,2)+pow(circle[i].first.second-circle[j].first.second,2));
                if(circle[i].second+circle[j].second>=d){
                    g[i].push_back(j);
                    g[j].push_back(i);
                }
            }
         }
         return !(bf(g,nocircle,nocircle+1,nocircle+2,nocircle+4)||bf(g,nocircle+3,nocircle+2,nocircle+1,nocircle+4));
    }
};
