class Solution {
public:
    bool dfs(int node, vector<vector<int>>& circles, vector<vector<int>>&adj, vector<int>&vis, int X, int Y, vector<int>&val){
        vis[node]=1;
        val[0]=min(val[0],circles[node][0]-circles[node][2]);
        val[1]=max(val[1],circles[node][0]+circles[node][2]);
        val[2]=min(val[2],circles[node][1]-circles[node][2]);
        val[3]=max(val[3],circles[node][1]+circles[node][2]);
        if((val[0]<=0 && val[1]>=X)||(val[2]<=0 && val[3]>=Y)||(val[0]<=0 && val[2]<=0)||(val[1]>=X && val[3]>=Y))
                return false;
        bool fl=true;
        for(auto x:adj[node])
        {
            if(!vis[x])
            {
                bool b=dfs(x,circles,adj,vis,X,Y,val);
                if(b==false)
                {
                    fl=false;
                    break;
                }
            }
        }
        return fl;
    }
    bool canReachCorner(int X, int Y, vector<vector<int>>& circles) {
        int n=circles.size();
        vector<vector<int>> adj(n);
        for(int i=0;i<n;i++)
        {
            for(int j=0;j<n;j++)
            {
                if(i!=j)
                {
                    long long ix=circles[i][0],iy=circles[i][1],ir=circles[i][2];
                    long long jx=circles[j][0],jy=circles[j][1],jr=circles[j][2];
                    long long d=(ix-jx)*(ix-jx)+(iy-jy)*(iy-jy);
                    long long r=(ir+jr)*(ir+jr);
                    if(d<=r)
                        adj[i].push_back(j);
                }
            }
        }
        vector<int> vis(n);
        bool ans=true;
        for(int i=0;i<n;i++)
        {
            if(!vis[i])
            {
                vector<int> val={INT_MAX,INT_MIN,INT_MAX,INT_MIN};
                bool b=dfs(i,circles,adj,vis,X,Y,val);
                if(b==false)
                {
                    ans=false;
                    break;
                }
            }
        }
        return ans;
    }
};
