from collections import deque
from math import sqrt

class Solution:
    def bfs(self, graph, start, target1, target2, nodes):
        queue = deque([start])
        visited = [False] * (nodes + 4)
        visited[start] = True

        while queue:
            u = queue.popleft()
            for v in graph[u]:
                if not visited[v]:
                    queue.append(v)
                    visited[v] = True

        return 1 if visited[target1] or visited[target2] else 0

    def canReachCorner(self, X, Y, circles2):
        circles = [(c[0], c[1], c[2]) for c in circles2]
        graph = [[] for _ in range(len(circles2) + 4)]

        numCircles = len(circles2)

        for i in range(numCircles):
            if circles[i][0] <= circles[i][2]:
                graph[numCircles].append(i)
                graph[i].append(numCircles)
            if X - circles[i][0] <= circles[i][2]:
                graph[numCircles + 2].append(i)
                graph[i].append(numCircles + 2)
            if circles[i][1] <= circles[i][2]:
                graph[numCircles + 1].append(i)
                graph[i].append(numCircles + 1)
            if Y - circles[i][1] <= circles[i][2]:
                graph[numCircles + 3].append(i)
                graph[i].append(numCircles + 3)

            for j in range(i + 1, numCircles):
                distance = sqrt((circles[i][0] - circles[j][0]) ** 2 + (circles[i][1] - circles[j][1]) ** 2)
                radiusSum = circles[i][2] + circles[j][2]
                if radiusSum >= distance:
                    graph[i].append(j)
                    graph[j].append(i)

        isBlocked = 0
        isBlocked = max(isBlocked, self.bfs(graph, numCircles, numCircles + 1, numCircles + 2, numCircles + 4))
        isBlocked = max(isBlocked, self.bfs(graph, numCircles + 3, numCircles + 2, numCircles + 1, numCircles + 4)) 

        return isBlocked == 0
