int n;
vector<vector<int>> adj;
vector<bool> used;
vector<int> comp;

void dfs(int v) {
    used[v] = true ;
    comp.push_back(v);
    for (int u : adj[v]) {
        if (!used[u])
            dfs(u);
    }
}

class Solution {
public:
    const int MOD = 1E9 + 7;
    bool checkIntersecting(vector<int> &c1, vector<int> &c2) {
        long long x2 = (c1[0] - c2[0]) * 1ll * (c1[0] - c2[0]);
        long long y2 = (c1[1] - c2[1]) * 1ll * (c1[1] - c2[1]);
        long long r2 = (c1[2] + c2[2]) * 1ll * (c1[2] + c2[2]);
        if (x2 + y2 > r2)
            return false;
        return true;
    }
    bool canReachCorner(int X, int Y, vector<vector<int>>& circles) {
        n = circles.size();
        adj = vector<vector<int>> (n);
        used = vector<bool> (n);

        for (int i = 0; i < n; i++) {
            for (int j = i + 1; j < n; j++) {
                if (checkIntersecting(circles[i], circles[j])) {
                    adj[i].push_back(j);
                    adj[j].push_back(i);
                }
            }
        }
        for (int v = 0; v < n; ++v) {
            if (!used[v]) {
                comp.clear();
                dfs(v);
                int xmin = MOD, xmax = -MOD, ymin = MOD, ymax = -MOD;
                // cout << "Component:" ;
                // for (int u : comp)
                //     cout << ' ' << u;
                // cout << endl ;
                for (auto &id: comp) {
                    int x = circles[id][0], y = circles[id][1], r = circles[id][2];
                    xmin = min(xmin, x - r);
                    xmax = max(xmax, x + r);
                    ymin = min(ymin, y - r);
                    ymax = max(ymax, y + r);
                    if (xmin <= 0 && xmax >= X)
                        return false;
                    if (ymin <= 0 && ymax >= Y)
                        return false;
                    if (xmin <= 0 && ymin <= 0)
                        return false;
                    if (xmax >= X && ymax >= Y)
                        return false;
                }
            }
        }
        // for (auto &it: cc) {
        //     auto &c = it.second;
        //     int xmin = MOD, xmax = -MOD, ymin = MOD, ymax = -MOD;
        //     for (auto &id: c) {
        //         int x = circles[id][0], y = circles[id][1], r = circles[id][2];
        //         xmin = min(xmin, x - r);
        //         xmax = max(xmax, x + r);
        //         ymin = min(ymin, y - r);
        //         ymax = max(ymax, y + r);
        //         if (xmin <= 0 && xmax >= X)
        //             return false;
        //         if (ymin <= 0 && ymax >= Y)
        //             return false;
        //         if (xmin <= 0 && ymin <= 0)
        //             return false;
        //         if (xmax >= X && ymax >= Y)
        //             return false;
        //     }
        // }
        return true;
    }
};
