class Solution {
public:
    bool canReachCorner(int X, int Y, vector<vector<int>>& circles) {
        int nc = circles.size();
        vector<vector<int>> g(nc + 4);

        for (int i = 0; i < nc; i++) {
            if (circles[i][0] <= circles[i][2]) {
                g[nc].push_back(i);
                g[i].push_back(nc);
            }
            if (X - circles[i][0] <= circles[i][2]) {
                g[nc + 2].push_back(i);
                g[i].push_back(nc + 2);
            }
            if (circles[i][1] <= circles[i][2]) {
                g[nc + 1].push_back(i);
                g[i].push_back(nc + 1);
            }
            if (Y - circles[i][1] <= circles[i][2]) {
                g[nc + 3].push_back(i);
                g[i].push_back(nc + 3);
            }
            for (int j = i + 1; j < nc; ++j) {
                double dist = sqrt(pow(circles[i][0] - circles[j][0], 2) + pow(circles[i][1] - circles[j][1], 2));
                int radSum = circles[i][2] + circles[j][2];
                if (radSum >= dist) {
                    g[i].push_back(j);
                    g[j].push_back(i);
                }
            }
        }

        auto bfs = [&](int s, int t1, int t2, int n) {
            queue<int> q;
            vector<bool> vis(n + 4, false);
            q.push(s);
            vis[s] = true;
            while (!q.empty()) {
                int u = q.front();
                q.pop();
                for (int v : g[u]) {
                    if (!vis[v]) {
                        q.push(v);
                        vis[v] = true;
                    }
                }
            }
            return (vis[t1] || vis[t2] ? 1 : 0);
        };

        int isBlocked = 0;
        isBlocked = max(isBlocked, bfs(nc, nc + 1, nc + 2, nc + 4));
        isBlocked = max(isBlocked, bfs(nc + 3, nc + 2, nc + 1, nc + 4));

        return isBlocked == 0;
    }
};
