class Solution {
public:
    long long dist(int a,int b,int c,int d){
        long long o=(long long)(c-a)*(long long)(c-a);
        long long p=(long long)(d-b)*(long long)(d-b);
        return o+p;
    }
    int par(vector<int>&a,int u){
        return (a[u]==-1)?u:par(a,a[u]);
    }
    void uni(vector<int>&parent,vector<int>&rank,int u,int v){
        u=par(parent,u);
        v=par(parent,v);
        if(u!=v){
            if(rank[u]<=rank[v]){
                swap(u,v);
            }
            rank[u]+=rank[v];
            parent[v]=u;
        }
    }
    bool canReachCorner(int x, int y, vector<vector<int>>& a) {
        vector<int>parent(a.size(),-1),rank(a.size(),1);
        for(int i=0;i<a.size();i++){
            for(int j=i+1;j<a.size();j++){
                if(i!=j && dist(a[i][0],a[i][1],a[j][0],a[j][1])<=((long long)(a[i][2]+a[j][2])*(long long)(a[i][2]+a[j][2]))){
                    // cout<<"ayush";
                    uni(parent,rank,i,j);
                }
            }
        }
        vector<int>is_l,is_u,is_r,is_d;
        for(int i=0;i<a.size();i++){
            if(a[i][0]<=a[i][2]){
                is_l.push_back(i);
            }
            if(x-a[i][0]<=a[i][2]){
                is_r.push_back(i);
            }
            if(a[i][1]<=a[i][2]){
                is_d.push_back(i);
            }
            if(y-a[i][1]<=a[i][2]){
                is_u.push_back(i);
            }
        }
        cout<<par(parent,0);
        for(int i=0;i<is_l.size();i++){
            for(int j=0;j<is_r.size();j++){
                int o=par(parent,is_l[i]);
                int p=par(parent,is_r[j]);
                if(o==p){
                    return false;
                }
            }
            for(int j=0;j<is_d.size();j++){
                int o=par(parent,is_l[i]);
                int p=par(parent,is_d[j]);
                if(o==p){
                    return false;
                }
            }
        }
        for(int i=0;i<is_d.size();i++){
            for(int j=0;j<is_u.size();j++){
               int o=par(parent,is_d[i]);
                int p=par(parent,is_u[j]);
                if(o==p){
                    return false;
                }
            }
        }
        for(int i=0;i<is_r.size();i++){
            for(int j=0;j<is_u.size();j++){
                int o=par(parent,is_r[i]);
                int p=par(parent,is_u[j]);
                if(o==p){
                    return false;
                }
            }
        }
        return true;
    }
};
