class Solution {
public:
    bool canReachCorner(int x, int y, vector<vector<int>>& c) {
        int n=c.size();
        vector<int> adj[n+4];
        for(int i=0;i<n;i++)
        {
            for(int j=i+1;j<n;j++)
            {  
                long long a=c[i][0],b=c[i][1],r=c[i][2],a2=c[j][0],b2=c[j][1],r2=c[j][2];
                if((a-a2)*(a-a2)+(b-b2)*(b-b2)<=(r+r2)*(r+r2))
                {
                    adj[i+4].push_back(j+4);
                    adj[j+4].push_back(i+4);
                }
            }
            if(abs(c[i][0])<=c[i][2])
           {
               adj[0].push_back(i+4);
               adj[i+4].push_back(0);
           }
           if(abs(c[i][1])<=c[i][2])
           {
                adj[1].push_back(i+4);
               adj[i+4].push_back(1);
           }
            if(abs(x-c[i][0])<=c[i][2])
            {
                adj[2].push_back(i+4);
               adj[i+4].push_back(2);
            }
            if(abs(y-c[i][1])<=c[i][2])
            {
                adj[3].push_back(i+4);
               adj[i+4].push_back(3);
            }
        }
        // cout<<sizeof(adj)/sizeof(int)<<endl;
        // for(int i=0;i<n+4;i++)
        // {
        //     cout<<i<<"--- ";
        //     for(auto it:adj[i]) cout<<it<<" ";cout<<endl;
        // }
        vector<int> vis(n+4,0);
        queue<int> q;
         vis[0]=1;
          vis[3]=1;
           for(auto it:adj[0])
           {
               q.push(it);
               vis[it]=1;
           }
        for(auto it:adj[3])
        {
            q.push(it);
               vis[it]=1;
        }
        while(!q.empty())
        {
            int node=q.front();
            // cout<<node<<endl;
            q.pop();
            if(node==1 || node==2) return false;
            for(auto it:adj[node])
            {
                if(vis[it]==0)
                {
                    q.push(it);
                    vis[it]=1;
                }
            }
        }
        return true;
    }
};
