class Solution {
public:
    double dist(long x, long y, long a, long b){
        return sqrt((a-x)*(a-x)+(b-y)*(b-y));
    }
    bool canReachCorner(int X, int Y, vector<vector<int>>& c) {

        map<vector<int>, int> mp;
        for(auto it: c)  mp[it]++;

        c.clear();
        for(auto it: mp){
            c.push_back(it.first);
        }
        int n=c.size();
        int sc=n, dest=n+1;  // n means x axis and n+1 means y axis
        queue<int> q;
        vector<vector<int>> adj(n+2);
        vector<int> vis(n+3, 0);
        for(int i=0;i<n;++i){
            if(c[i][1]-c[i][2]<=0 || c[i][0]+c[i][2]>=X) { // touches down , right
                // adj[i].push_back(n);
                q.push(i);
                vis[i]=1;
                if(c[i][0]-c[i][2]<=0 || c[i][1]+c[i][2]>=Y)  return 0;
            }
            else if(c[i][0]-c[i][2]<=0 || c[i][1]+c[i][2]>=Y){  // touches left, up
                adj[i].push_back(n+1);
            }
            for(int j=0;j<n;++j){
                if(j==i)  continue;

                double d=dist(c[i][0], c[i][1], c[j][0], c[j][1]);
                if(d<=(double)(c[i][2]+c[j][2])){
                    adj[i].push_back(j);
                    // adj[j].push_back(i);
                }
            }
        }
        // for(int i=0;i<n;++i){
        //     cout<<i<<":";
        //     for(auto it: adj[i]){
        //         cout<<it<<" ";
        //     }
        //     cout<<"\n";
        // }
        // cout<<dist(3, 7, 1, 5)<<"\n";
        while(!q.empty()){
            auto node=q.front();  q.pop();
            // cout<<node<<"\n";
            if(node==n+1)  return 0;

            for(auto it: adj[node]){
                if(!vis[it]){
                    vis[it]=1;
                    q.push(it);
                }
            }
        }
        return 1;
    }
};
