class Solution {

public:

    int bf(vector<vector<int>>& g, int s, int t1, int t2, int n) {

        queue<int> q;

        vector<int> vis(n + 4, 0);

        q.push(s);

        vis[s] = 1;



        while (!q.empty()) {

            int u = q.front();

            q.pop();

            for (int v : g[u]) {

                if (!vis[v]) {

                    q.push(v);

                    vis[v] = 1;

                }

            }

        }

        return vis[t1] || vis[t2];

    }



    bool canReachCorner(int X, int Y, vector<vector<int>>& c2) {

        int nc = c2.size();

        vector<pair<pair<int, int>, int>> c(nc);

        vector<vector<int>> g(nc + 4);



        for (int i = 0; i < nc; ++i) {

            c[i] = {{c2[i][0], c2[i][1]}, c2[i][2]};

        }



        for (int i = 0; i < nc; ++i) {

            if (c[i].first.first <= c[i].second) {

                g[nc].push_back(i);

                g[i].push_back(nc);

            }

            if (X - c[i].first.first <= c[i].second) {

                g[nc + 2].push_back(i);

                g[i].push_back(nc + 2);

            }

            if (c[i].first.second <= c[i].second) {

                g[nc + 1].push_back(i);

                g[i].push_back(nc + 1);

            }

            if (Y - c[i].first.second <= c[i].second) {

                g[nc + 3].push_back(i);

                g[i].push_back(nc + 3);

            }

            for (int j = i + 1; j < nc; ++j) {

                double d = sqrt(pow(c[i].first.first - 

                c[j].first.first, 2) +

                pow(c[i].first.second - c[j].first.second, 2));

                if (c[i].second + c[j].second >= d) {

                    g[i].push_back(j);

                    g[j].push_back(i);

                }

            }

        }



        return !(bf(g, nc, nc + 1, nc + 2, nc + 4) || 

                 bf(g, nc + 3, nc + 2, nc + 1, nc + 4));

    }

};
