class Solution {
    public boolean canReachCorner(int X, int Y, int[][] circles) {

        int numCircles = circles.length;
        List<List<Integer>> graph = new ArrayList<>();
        for (int i = 0; i < numCircles + 4; i++) {
            graph.add(new ArrayList<>());
        }

        for (int i = 0; i < numCircles; i++) {
            int x = circles[i][0];
            int y = circles[i][1];
            int r = circles[i][2];
            if (x <= r) {
                graph.get(numCircles).add(i);
                graph.get(i).add(numCircles);
            }
            if (X - x <= r) {
                graph.get(numCircles + 2).add(i);
                graph.get(i).add(numCircles + 2);
            }
            if (y <= r) {
                graph.get(numCircles + 1).add(i);
                graph.get(i).add(numCircles + 1);
            }
            if (Y - y <= r) {
                graph.get(numCircles + 3).add(i);
                graph.get(i).add(numCircles + 3);
            }
            for (int j = i + 1; j < numCircles; j++) {
                int xj = circles[j][0];
                int yj = circles[j][1];
                int rj = circles[j][2];
                double distance = Math.sqrt(Math.pow(x - xj, 2) + Math.pow(y - yj, 2));
                double radiusSum = r + rj;
                if (radiusSum >= distance) {
                    graph.get(i).add(j);
                    graph.get(j).add(i);
                }
            }
        }

        int isBlocked = 0;
        isBlocked = Math.max(isBlocked, bfs(graph, numCircles, numCircles + 1, numCircles + 2, numCircles + 4));  // Check for bottom-left corner
        isBlocked = Math.max(isBlocked, bfs(graph, numCircles + 3, numCircles + 2, numCircles + 1, numCircles + 4));  // Check for top-right corner

        return isBlocked == 0;
    }

    public static int bfs(List<List<Integer>> graph, int start, int target1, int target2, int nodes) {
        Queue<Integer> queue = new LinkedList<>();
        queue.add(start);
        boolean[] visited = new boolean[nodes + 4];
        visited[start] = true;

        while (!queue.isEmpty()) {
            int u = queue.poll();
            for (int v : graph.get(u)) {
                if (!visited[v]) {
                    queue.add(v);
                    visited[v] = true;
                }
            }
        }
        return (visited[target1] || visited[target2]) ? 1 : 0;

    }
}
