class Solution:
    def canReachCorner(self, X: int, Y: int, circles: List[List[int]]) -> bool:
        def isin(a,b,cir):
            x,y,r=cir
            if (x-a)**2+(y-b)**2<=r**2:
                return True
            return False
        def isconn(cir1,cir2):
            x1,y1,r1=cir1
            x2,y2,r2=cir2
            if (x1-x2)**2+(y1-y2)**2<=(r1+r2)**2:
                return True
            return False
        n=len(circles)
        root=list(range(n))
        constatus=[0]*n 
        def find(x):
            nonlocal root
            if root[x]==x:
                return x
            root[x]=find(root[x])
            return root[x]
        def union(i,j):
            nonlocal root
            nonlocal constatus
            if find(i)!=find(j):
                rj=root[j]
                root[rj]=i
                # print("joining ",i,j,constatus[root[i]],constatus[rj])
                constatus[root[i]]|=constatus[rj]

        bound=set([3,6,7,9,11,12,13,14,15])
        for i in range(n):
            cir1=circles[i]
            if isin(0,0,cir1) or isin(X,Y,cir1):
                return False
            a,b,_,=cir1
            # top of rec, X axis, Y axis, right of rec
            if isin(a,Y,cir1):
                constatus[i]^=1
            if isin(a,0,cir1):
                constatus[i]^=2
            if isin(0,b,cir1):
                constatus[i]^=4
            if isin(X,b,cir1):
                constatus[i]^=8
            if constatus[i] in bound:
                return False
        # print("ori conn",constatus)
        for i in range(n):
            cir1=circles[i]
            for j in range(i+1,n):
                cir2=circles[j]
                if isconn(cir1,cir2):
                    union(i,j)
            if constatus[find(i)] in bound:
                return False
        # print(root)
        # print(constatus)
        return True
