typedef pair<ll,ll> P;

int p[200001],r[200001];
void init(ll n){rep(i,n)p[i]=i,r[i]=0;}
int find(int x){return (p[x]==x)?x:(p[x]=find(p[x]));}
void unite(int x,int y) {
  x=find(x),y=find(y);
  if(x==y)return;
  if(r[x]<r[y])p[x]=y;
  else{p[y]=x;if(r[x]==r[y])r[x]++;}
}
bool same(int x,int y){return find(x)==find(y);}

double D(P a,P b) {
  return sqrt((a.F-b.F)*(a.F-b.F)+(a.S-b.S)*(a.S-b.S));
}

class Solution {
public:
  bool canReachCorner(int x, int y, vector<vector<int>>& c) {
    ll n=c.size();
    init(n+2);
    rep(i,n) {
      REP(j,i+1,n) {
        if(D(P(c[i][0],c[i][1]),P(c[j][0],c[j][1]))<=c[i][2]+c[j][2]) unite(i,j);
      }
      if(c[i][0]<=c[i][2]) unite(i,n);
      if(y-c[i][1]<=c[i][2]) unite(i,n);
      if(c[i][1]<=c[i][2]) unite(i,n+1);
      if(x-c[i][0]<=c[i][2]) unite(i,n+1);
    }
    if(same(n,n+1)) return 0;
    return 1;
  }
};
