#define ll long long

ll N = 200005;
vector<ll> vec(N, -1);
vector<ll> sz(N, 1);
vector<ll> min_x(N, 1);
vector<ll> max_x(N, 1);
vector<ll> min_y(N, 1);
vector<ll> max_y(N, 1);
void init(ll N){
    for(ll i=0;i<N;i++){
        vec[i]=i;
        sz[i]=1;
        min_x[i]=N;
        min_y[i]=N;
        max_x[i]=0;
        max_y[i]=0;
    }
}
ll find(ll a){return (vec[a] == a ? a : vec[a] = find(vec[a]));}
bool merge(ll a, ll b){
    ll x = find(a);
    ll y = find(b);
    if (x == y)return true;
    if (sz[x] > sz[y])swap(x,y);
    sz[x]+=sz[y];
    vec[y] = x;
    min_x[x]=min(min_x[x],min_x[y]);
    min_y[x]=min(min_y[x],min_y[y]);
    max_x[x]=max(max_x[x],max_x[y]);
    max_y[x]=max(max_y[x],max_y[y]);
    return false;
}

bool intersect(vector<int> &a,vector<int> &b){
    ll lhs=a[2]+b[2];
    lhs*=lhs;
    ll rhs=1LL*abs(a[0]-b[0])*abs(a[0]-b[0])+1ll*abs(a[1]-b[1])*abs(a[1]-b[1]);
    return lhs>=rhs;
}

class Solution {
public:
    bool canReachCorner(int X, int Y, vector<vector<int>>& circles) {
        ll n=circles.size();
        init(n);
        for(ll i=0;i<n;i++){
            min_x[i]=max(0,circles[i][0]-circles[i][2]);
            max_x[i]=min(X,circles[i][0]+circles[i][2]);
            min_y[i]=max(0,circles[i][1]-circles[i][2]);
            max_y[i]=min(Y,circles[i][1]+circles[i][2]);
        }
        for(ll i=0;i<n;i++){
            for(ll j=i+1;j<n;j++){
                if(intersect(circles[i],circles[j])){
                    merge(find(i),find(j));
                }
            }
        }
        for(ll i=0;i<n;i++){
            ll flag=0;
            ll x=find(i);
            // cout<<x<<" "<<min_x[x]<<" "<<min_y[x]<<" "<<max_x[x]<<" "<<max_y[x]<<endl;
            if(min_x[x]==0 && min_y[x]==0)flag++;
            if(min_x[x]==0 && max_x[x]==X)flag++;
            if(max_y[x]==Y && min_y[x]==0)flag++;
            if(max_x[x]==X && max_y[x]==Y)flag++;
            if(flag)return false;
        }
        return true;
    }
};
