class Solution:
    def canReachCorner(self, X: int, Y: int, circles: List[List[int]]) -> bool:
        for c in circles:
            if math.dist((0, 0), (c[0], c[1])) <= c[2]:
                return False
            if math.dist((X, Y), (c[0], c[1])) <= c[2]:
                return False

        n = len(circles)
        parent = [i for i in range(n)]

        def get_parent(i):
            if parent[i] != i:
                parent[i] = get_parent(parent[i])
            return parent[i]

        for i in range(n - 1):
            for j in range(i + 1, n):
                if math.dist((circles[i][0], circles[i][1]), (circles[j][0], circles[j][1])) <= circles[i][2] + \
                        circles[j][2]:
                    a = get_parent(i)
                    b = get_parent(j)
                    if a != b:
                        parent[a] = b

        touch = [0] * n

        for i in range(n):
            c = circles[i]
            p = get_parent(i)

            dx = abs(c[0])
            if dx <= c[2]:
                d = math.sqrt(c[2] * c[2] - dx * dx)
                l = c[1] - d
                r = c[1] + d
                if l <= Y and r >= 0:
                    touch[p] |= 1

            dy = abs(Y - c[1])
            if dy <= c[2]:
                d = math.sqrt(c[2] * c[2] - dy * dy)
                l = c[0] - d
                r = c[0] + d
                if l <= X and r >= 0:
                    touch[p] |= 1

            dx = abs(X - c[0])
            if dx <= c[2]:
                d = math.sqrt(c[2] * c[2] - dx * dx)
                l = c[1] - d
                r = c[1] + d
                if l <= Y and r >= 0:
                    touch[p] |= 2

            dy = abs(c[1])
            if dy <= c[2]:
                d = math.sqrt(c[2] * c[2] - dy * dy)
                l = c[0] - d
                r = c[0] + d
                if l <= X and r >= 0:
                    touch[p] |= 2

            if touch[p] == 3:
                return False

        return True
