class Solution {
    typedef long long ll;
public:
    bool canReachCorner(int X, int Y, vector<vector<int>>& circles) {
        sort(circles.begin(),circles.end());
        int n=circles.size();
        vector<bool> lb(n,0),rb(n,0);
        for(int i=0;i<n;i++){
            int x=circles[i][0],y=circles[i][1],r=circles[i][2];
            if((x-r<=0) || (y+r>=Y)){
                lb[i]=1;
            }
            if((y-r<=0) || (x+r>=X)){
                rb[i]=1;
            }
            for(int j=i-1;j>=0;j--){
                int x2=circles[j][0],y2=circles[j][1],r2=circles[j][2];
                ll num1=1LL*(r+r2)*(r+r2);
                ll num2=1LL*(x2-x)*(x2-x)+1LL*(y2-y)*(y2-y);
                if(num1>=num2){
                    lb[i]=lb[i] || lb[j];
                    rb[i]=rb[i] || rb[j];
                    lb[j]=lb[i];
                    rb[j]=rb[i];
                }
            }
            if(lb[i] && rb[i])return 0;
        }
        return 1;
    }
};
