class Solution {
public:

    bool isIntersection(vector<int> &first, vector<int> &second)
    {

        long long distance = 1LL * (first[0] - second[0]) * (first[0] - second[0]) + 1LL * (first[1] - second[1]) * (first[1] - second[1]);
        long long anotherDistance = 1LL * (first[2] + second[2]) * (first[2] + second[2]);
        return distance <= anotherDistance; 
    }
    bool canReachCorner(int X, int Y, vector<vector<int>>& circles) {
        int n = circles.size();
        vector<int> rank(n);
        vector<vector<int>> comp(n);
        for(int i = 0; i < n; i++)
        {
            rank[i] = i;
            int x = circles[i][0], y = circles[i][1], r = circles[i][2];
            comp[i] = {x - r, y - r, x + r, y + r};
        }
        for(int i = 0; i < n; i++)
        {
            for(int j = i + 1; j < n; j++)
            {
                if(isIntersection(circles[i], circles[j]))
                {
                    int mn = min(rank[i], rank[j]);
                    rank[i] = mn, rank[j] = mn;
                    int left, up, right, down;
                    left = min(comp[i][0], comp[j][0]);
                    down = min(comp[i][1], comp[j][1]);
                    right = max(comp[i][2], comp[j][2]);
                    up = max(comp[i][3], comp[j][3]);
                    comp[i][0] = left, comp[j][0] = left;
                    comp[i][1] = down, comp[j][1] = down;
                    comp[i][2] = right, comp[j][2] = right;
                    comp[i][3] = up, comp[j][3] = up;
                }
            }
        }
        for(int i = 0; i < n; i++)
        {
             bool is_left = comp[i][0] <= 0;
             bool is_down = comp[i][1] <= 0;
             bool is_right = comp[i][2] >= X;
             bool is_upper = comp[i][3] >= Y;
             if((is_right || is_down) && (is_left || is_upper))
                return false;
        }
        return true;
    }
};
