class Solution {
public:

    bool bad(vector<int>& c, int X, int Y){
        return true;
    }
    bool circleTouchCircle(vector<int>& a, vector<int>& b){
        long long xDist = (long long) (a[0] - b[0]) * (a[0] - b[0]);
        long long yDist = (long long) (a[1] - b[1]) * (a[1] - b[1]);
        long long sum = xDist + yDist;
        long long maxDist = (long long) (a[2] + b[2]) * (a[2] + b[2]);
        return maxDist >= sum;
    }
    bool circleTouchSegment(vector<pair<int,int>>& l, vector<int>& c){
        if(l[0].first == l[1].first){
            // x = l[0].first is the line
            long long distaway = abs(c[0] - l[0].first);
            return c[2] >= distaway;
        }else{
            // y = l[0].second is the line
            long long distaway = abs(c[1] - l[0].second);
            return c[2] >= distaway;
        }
    }

    void dfs(int node, vector<bool>& v, vector<vector<int>>& adj){
        if(v[node]){
            return;
        }
        v[node] = true;
        for(auto u : adj[node]){
            dfs(u, v, adj);
        }
    }

    bool canReachCorner(int X, int Y, vector<vector<int>>& circles) {
        int n = circles.size();
        vector<bool> valid(n, true);
        for(int i = 0; i < n; i++){
            if(bad(circles[i], X, Y)){
                valid[i] = false;
            }
        }
        vector<vector<int>> adj(n + 4);
        for(int i = 0; i < n; i++){
            for(int j = i + 1; j < n; j++){
                if(circleTouchCircle(circles[i], circles[j])){
                    adj[i].push_back(j);
                    adj[j].push_back(i);
                }
            }
        }
        vector<vector<pair<int,int>>> lines;
        lines.push_back({{0,0}, {X,0}});
        lines.push_back({{0,0}, {0,Y}});
        lines.push_back({{0,Y}, {X,Y}});
        lines.push_back({{X,0}, {X,Y}});
        for(int i = 0; i < 4; i++){
            for(int j = 0; j < n; j++){
                if(circleTouchSegment(lines[i], circles[j])){
                    adj[n + i].push_back(j);
                    adj[j].push_back(n + i);
                }
            }
        }
        vector<bool> v(n + 4);
        dfs(n, v, adj);
        if(v[n + 1] || v[n + 2]){
            return false;
        }
        vector<bool> v2(n + 4);
        dfs(n + 1, v2, adj);
        if(v2[n + 3]){
            return false;
        }
        vector<bool> v3(n + 4);
        dfs(n + 2, v3, adj);
        if(v3[n + 3]){
            return false;
        }
        return true;
    }
};
