class Solution {
public:
    long long sq(long long x) {
        return x * x;
    }
    bool canReachCorner(int X, int Y, vector<vector<int>>& circles) {
        int n = circles.size();
        vector<int> x(n), y(n), r(n);
        for (int i = 0; i < n; i++) {
            x[i] = circles[i][0];
            y[i] = circles[i][1];
            r[i] = circles[i][2];
        }
        vector<vector<int>> adj(n);
        for (int i = 0; i < n; i++) {
            for (int j = i + 1; j < n; j++) {
                if (sq(x[i] - x[j]) + sq(y[i] - y[j]) <= sq(r[i] + r[j])) {
                    adj[i].push_back(j);
                    adj[j].push_back(i);
                }
            }
        }
        vector<int> vis(n);
        for (int i = 0; i < n; i++) {
            if (vis[i]) continue;
            vector<int> q{i};
            vis[i] = 1;
            for (int j = 0; j < q.size(); j++) {
                int x = q[j];
                for (auto y : adj[x]) {
                    if (!vis[y]) {
                        vis[y] = 1;
                        q.push_back(y);
                    }
                }
            }
            for (int j = 0; j < q.size(); j++) {
                for (int k = j; k < q.size(); k++) {
                    int a = q[j], b = q[k];
                    for (int z = 0; z < 2; z++) {
                        if (x[a] <= r[a] && x[b] + r[b] >= X) return 0;
                        if (y[a] <= r[a] && y[b] + r[b] >= Y) return 0;
                        if (x[a] <= r[a] && y[b] <= r[b]) return 0;
                        if (x[a] + r[a] >= X && y[b] + r[b] >= Y) return 0;
                        swap(a, b);
                    }
                }
            }
        }
        return 1;
    }
};
