class UnionFind:
    def __init__(self, n):
        self.parent = list(range(n))
        self.rank = [0] * n

    def find(self, x):
        if self.parent[x] != x:
            self.parent[x] = self.find(self.parent[x])
        return self.parent[x]

    def union(self, x, y):
        rootX = self.find(x)
        rootY = self.find(y)
        if rootX != rootY:
            if self.rank[rootX] > self.rank[rootY]:
                self.parent[rootY] = rootX
            elif self.rank[rootX] < self.rank[rootY]:
                self.parent[rootX] = rootY
            else:
                self.parent[rootY] = rootX
                self.rank[rootX] += 1

class Solution:
    def canReachCorner(self, X: int, Y: int, circles: List[List[int]]) -> bool:
        n = len(circles)
        uf = UnionFind(n + 4) 

        LEFT_EDGE = n
        RIGHT_EDGE = n + 1
        TOP_EDGE = n + 2
        BOTTOM_EDGE = n + 3

        def touches_left(x, r): return x - r <= 0
        def touches_right(x, r): return x + r >= X
        def touches_bottom(y, r): return y - r <= 0
        def touches_top(y, r): return y + r >= Y

        for i in range(n):
            x, y, r = circles[i]
            if touches_left(x, r):
                uf.union(i, LEFT_EDGE)
            if touches_right(x, r):
                uf.union(i, RIGHT_EDGE)
            if touches_bottom(y, r):
                uf.union(i, BOTTOM_EDGE)
            if touches_top(y, r):
                uf.union(i, TOP_EDGE)

        for i in range(n):
            for j in range(i + 1, n):
                x1, y1, r1 = circles[i]
                x2, y2, r2 = circles[j]
                if (x1 - x2) ** 2 + (y1 - y2) ** 2 <= (r1 + r2) ** 2:
                    uf.union(i, j)

        if (uf.find(LEFT_EDGE) == uf.find(RIGHT_EDGE) or
            uf.find(TOP_EDGE) == uf.find(BOTTOM_EDGE) or
            uf.find(LEFT_EDGE) == uf.find(BOTTOM_EDGE) or
            uf.find(TOP_EDGE) == uf.find(RIGHT_EDGE)):
            return False

        return True
